import React, { useState } from 'react';
import './Login.css';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import logo from '../../../assets/logo.png';
import { useHistory } from 'react-router-dom';

const useStyles = makeStyles({
  root: {
    width:"50%",
    marginLeft:"35%"
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },

});





function Login() {

  const classes = useStyles();
  const history = useHistory();


  //const bull = <span className={classes.bullet}>•</span>;

  const [username, setUserName] = useState();
  const [password, setPassword] = useState();


  const handleChangeEvent = (e) => {
    const input = e.target.name;
    if (input === "username") {
        setUserName(e.target.value);

    } else { setPassword(e.target.value) }
    console.log("username :", username);
    console.log("Password :", password);
}

const handleSubmit = () => {
    if (username == "" && password == "") {            
        alert("Logged In");
        history.push("/DashboardMain");
    } else {
        alert("wrong password");
        console.log("chalra ba");
    }
    history.push('/CreateUser');
}



  return (
    <div className="LogIn">
      <div className="row" style={{width:"100%"}}>

        <div className="col-sm-6 imageDiv">
          <div >            
            <img src={logo} style={{ width: "252px", height: "100px" }} />
            
          </div>
        </div>
        
        <div className="col-sm-6 CardDiv">
          <div >
            <Card className={classes.root}>
              <CardContent>
                <div className="col-md-12" style={{ marginTop: "30px" }}>
                  <TextField id="outlined-basic" label="UserName" style={{ width: "200px" }}
                                             
                                             id="username"
                                             name="username"
                                             onChange={(e) => handleChangeEvent(e)}

                  />
                </div>
                <div className="col-md-12" style={{ marginTop: "30px" }} >
                  <TextField id="outlined-basic" label="Password" style={{ width: "200px" }}

                                              id="password"
                                              
                                              name="password"
                                              onChange={(e) => handleChangeEvent(e)}

                  />
                </div>
                <Button variant="contained" color="primary" style={{ marginTop: "10%" }}
                onClick={handleSubmit}
                >
                  Login
                </Button>

              </CardContent>
              <CardActions>
              </CardActions>
            </Card>


          </div>

        </div>

      </div>

    </div>
  );
}

export default Login;
