import React, { useState } from 'react';
import './Login.css';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { useHistory } from 'react-router-dom';
import logo from '../../../assets/logo.png';
import iconsimage from '../../../assets/iconsimage.png';


const useStyles = makeStyles({
  root: {
    width: "50%",
    marginLeft: "30%"
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  img:{
    width:200,
    height:200,
  }


});





function Login() {

  const classes = useStyles();
  const history = useHistory();



  //const bull = <span className={classes.bullet}>â€¢</span>;

  const [username, setUserName] = useState();
  const [password, setPassword] = useState();



  const handleChangeEvent = (e) => {
    const input = e.target.name;
    if (input === "username") {
      setUserName(e.target.value);

    } else { setPassword(e.target.value) }
    console.log("username :", username);
    console.log("Password :", password);
  }


  const handleSubmit = () => {
    if (username == "admin" && password == "admin") {
      alert("Logged In");
      history.push("/CreateUser");
    } else {
      alert("wrong password");
      console.log("chalra ba");
    }
  }



  return (
    <div className="LogIn">
      <div className="row" style={{ width: "100%" }}>

        <div className="col-sm-6 imageDiv">
          <div >
            <img src={iconsimage} style={{ width: "350px", height: "350px" }} />

          </div>
        </div>

        <div className="col-sm-6 CardDiv">
          <div >
            <Card className={classes.root}>
              <CardContent>
                <div className="col-md-12" style={{ marginTop: "50px" }}>
                  <TextField id="outlined-basic" label="UserName" style={{ width: "200px" }}

                    id="username"
                    name="username"
                    onChange={(e) => handleChangeEvent(e)}

                  />
                </div>
                <div className="col-md-12" style={{ marginTop: "30px" }} >
                  <TextField id="outlined-basic" label="Password" style={{ width: "200px" }}

                    id="password"

                    name="password"
                    onChange={(e) => handleChangeEvent(e)}

                  />
                </div>
                <Button variant="contained" color="primary" style={{ marginTop: "10%" }}
                  onClick={handleSubmit}
                >
                  Login
                </Button>

              </CardContent>
              <CardActions>
              </CardActions>
            </Card>


          </div>

        </div>

      </div>

    </div>
  );
}

export default Login;
